module.exports = async function(data) {
    const q = data.submitted_questions[0];
	const q_type = q.question_type;
	let submitted_answer;
    if (q_type === "q_mc_single") {
        submitted_answer = q.submitted_ans_q_mc_single;
    } else if (q_type === "q_mc_multi") {
        submitted_answer = JSON.stringify(q.submitted_ans_q_mc_multi);
    } else if (q_type === "q_mc_binary") {
        submitted_answer = q.submitted_ans_q_mc_binary;
    } else if (q_type === "q_mc_flexible") {
        submitted_answer = JSON.stringify(q.submitted_ans_q_mc_flexible);
    }
	// 为避免类型混乱，先全都转化为字符串来存储。
	return submitted_answer;
}

