module.exports = async function(data) {
    const q = data.submitted_questions[0];
    // 注意submitted_question是个列表，我们每次只更新一个所以只管列表第一项。
	const q_type = q.question_type;
    const answer = data.item_read_questions[0].answer;

    const submitted_answer = data.submitted_answer;

    function arraysEqual(arr1, arr2) {
        // 如果长度不同，直接返回 false
        if (arr1.length !== arr2.length) {
            return false;
        }

        // 排序后逐个比较元素
        arr1.sort();
        arr2.sort();

        // 比较每个元素
        for (let i = 0; i < arr1.length; i++) {
            if (arr1[i] !== arr2[i]) {
                return false;
            }
        }

        // 如果所有元素都相同，返回 true
        return true;
    }

    let true_status = 0;
    if ( ["q_mc_single", "q_mc_binary"].includes(q_type) ){
        if (answer === submitted_answer) {
            true_status = 1;
        } else {
            true_status = 0;
        }
    } else if ( ["q_mc_multi", "q_mc_flexible"].includes(q_type) ){
        const answer_json = JSON.parse(answer);
        const submitted_answer_json = JSON.parse(submitted_answer)
        if ( submitted_answer_json.length === 0) {
			// 如果提交的答案是空列表，直接0分。
            true_status = 0;
        } else if (submitted_answer_json.every(element => answer_json.includes(element))) {
            // 如果包含关系
            if (arraysEqual(submitted_answer_json, answer_json)) {
                // 如果完全相等
                true_status = 1;
            } else {
                true_status = 0.5;
            }
        } else {
            true_status = 0;
        }
    }

    const score = true_status * q.point_value

    return {
        answer,
        submitted_answer,
        true_status,
        score
    };
}
